import { Component } from '@angular/core';
import {ApiService} from "../services/api/api.service";
import {Observable, of} from "rxjs";
import {Link} from "../models/link.model";
import {LinkService} from "../services/link/link.service";

@Component({
  selector: 'app-tab1',
  templateUrl: 'tab1.page.html',
  styleUrls: ['tab1.page.scss']
})
export class Tab1Page {

  links$: Observable<Link>[] = [];
  category: string = "";
  blacklistFlag: string[] = [];
  type: string = "";

  constructor(
    private apiService: ApiService,
    private linkService: LinkService
  ) {}

  handleCategory(e: any){
    this.category = e.detail.value;
  }

  handleBlacklist(e: any){
    this.blacklistFlag = e.detail.value;
  }

  handleType(e: any){
    this.type = e.detail.value;
  }

  openJoke(link: Link){
    this.linkService.linkDetail = link;
  }

  link1: Link={category: this.category, blacklistFlag:this.blacklistFlag,type: this.type}
  generateLink(){


    this.link1 = {category: this.category, blacklistFlag:this.blacklistFlag,type: this.type}
    this.links$.push(of(this.link1));
  }

}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-title>
      Jokes
    </ion-title>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Jokes</ion-title>
    </ion-toolbar>
  </ion-header>



    <ion-list>
      <ion-item>
        <ion-select interface="popover" placeholder="Select category" (ionChange)="handleCategory($event)">
          <ion-select-option value="any">Any</ion-select-option>
          <ion-select-option value="misc">Misc</ion-select-option>
          <ion-select-option value="programming">Programming</ion-select-option>
          <ion-select-option value="dark">Dark</ion-select-option>
          <ion-select-option value="pun">Pun</ion-select-option>
          <ion-select-option value="spooky">Spooky</ion-select-option>
          <ion-select-option value="christmas">Christmas</ion-select-option>
        </ion-select>
      </ion-item>
    </ion-list>

    <ion-list>
      <ion-item>
        <ion-select interface="popover" placeholder="Select blacklistFlags" [multiple]="true" (ionChange)="handleBlacklist($event)">
          <ion-select-option value="nsfw">Nsfw</ion-select-option>
          <ion-select-option value="religious">Religious</ion-select-option>
          <ion-select-option value="political">Political</ion-select-option>
          <ion-select-option value="racist">Racist</ion-select-option>
          <ion-select-option value="sexist">Sexist</ion-select-option>
          <ion-select-option value="explicit">Explicit</ion-select-option>
        </ion-select>
      </ion-item>
    </ion-list>

    <ion-list>
      <ion-item>
        <ion-select interface="popover" placeholder="Select type" (ionChange)="handleType($event)">
          <ion-select-option value="single">Single</ion-select-option>
          <ion-select-option value="twopart">Two part</ion-select-option>
        </ion-select>
      </ion-item>
    </ion-list>



    <ng-container *ngFor="let item$ of links$">

      <div *ngIf="item$ | async; let link">
        <div *ngIf="link.blacklistFlag.length === 0; else bFlagBiggerThan0">
          <ion-card routerLink="/joke-print" (click)="openJoke(link)">Generate joke with category:{{link.category}}, type:{{link.type}}</ion-card>
        </div>
        <ng-template #bFlagBiggerThan0> <!--generuje se jenom pokud platí podmínka else v ngIf -->
          <ion-card routerLink="/joke-print" (click)="openJoke(link)">Generate joke with category:{{link.category}}, blacklistFlags:{{link.blacklistFlag}}, type:{{link.type}}</ion-card>
        </ng-template>
      </div>

    </ng-container>




  <ion-button (click)="generateLink()">Generate link</ion-button>


</ion-content>
